function validateForm() {
    var isValid = true;

    // Ambil nilai dari setiap input di form pertama
    var namaEvent = document.getElementById("nama_event").value.trim();
    var penyelenggaraEvent = document
        .getElementById("penyelenggara_event")
        .value.trim();
    var uploadKTP = document.getElementById("upload_ktp").value.trim();
    var kategoriEvent = document.getElementsByName("kategori_event")[0].value;
    var tanggalEvent = document.getElementById("tanggal_event").value.trim();
    var jamEvent = document.getElementById("jam_event").value.trim();
    var tanggalPendaftaran = document
        .getElementsByName("tanggal_pendaftaran")[0]
        .value.trim();
    var tanggalPenutupan = document
        .getElementsByName("tanggal_penutupan")[0]
        .value.trim();
    var deskripsi = document.getElementById("deskripsi").value.trim();
    var alamat = document.getElementById("alamat").value.trim();

    // Validasi untuk setiap input di form pertama
    if (namaEvent === "") {
        document.getElementById("nama_event").setCustomValidity("Harap diisi");
        isValid = false;
    } else {
        document.getElementById("nama_event").setCustomValidity("");
    }

    if (penyelenggaraEvent === "") {
        document
            .getElementById("penyelenggara_event")
            .setCustomValidity("Harap diisi");
        isValid = false;
    } else {
        document.getElementById("penyelenggara_event").setCustomValidity("");
    }

    // Lakukan validasi untuk setiap input di form pertama
    if (uploadKTP === "") {
        document.getElementById("upload_ktp").setCustomValidity("Harap diisi");
        isValid = false;
    } else {
        document.getElementById("upload_ktp").setCustomValidity("");
    }

    if (kategoriEvent === "Pilih Kategori") {
        document
            .getElementsByName("kategori_event")[0]
            .setCustomValidity("Harap diisi");
        isValid = false;
    } else {
        document.getElementsByName("kategori_event")[0].setCustomValidity("");
    }

    if (tanggalEvent === "") {
        document
            .getElementById("tanggal_event")
            .setCustomValidity("Harap diisi");
        isValid = false;
    } else {
        document.getElementById("tanggal_event").setCustomValidity("");
    }

    if (jamEvent === "") {
        document.getElementById("jam_event").setCustomValidity("Harap diisi");
        isValid = false;
    } else {
        document.getElementById("jam_event").setCustomValidity("");
    }

    if (tanggalPendaftaran === "") {
        document
            .getElementsByName("tanggal_pendaftaran")[0]
            .setCustomValidity("Harap diisi");
        isValid = false;
    } else {
        document
            .getElementsByName("tanggal_pendaftaran")[0]
            .setCustomValidity("");
    }

    if (tanggalPenutupan === "") {
        document
            .getElementsByName("tanggal_penutupan")[0]
            .setCustomValidity("Harap diisi");
        isValid = false;
    } else {
        document
            .getElementsByName("tanggal_penutupan")[0]
            .setCustomValidity("");
    }

    if (deskripsi === "") {
        document.getElementById("deskripsi").setCustomValidity("Harap diisi");
        isValid = false;
    } else {
        document.getElementById("deskripsi").setCustomValidity("");
    }

    if (alamat === "") {
        document.getElementById("alamat").setCustomValidity("Harap diisi");
        isValid = false;
    } else {
        document.getElementById("alamat").setCustomValidity("");
    }

    return isValid;
}


