const prevBtns = document.querySelectorAll(".btn-prev");
const nextBtns = document.querySelectorAll(".btn-next");
const progress = document.getElementById("progress1");
const formSteps = document.querySelectorAll(".form-step1");
const progressSteps = document.querySelectorAll(".progress-step1");

let formStepsNum = 0;

nextBtns.forEach((btn) => {
    btn.addEventListener("click", () => {
        formStepsNum++;
        updateFormSteps();
        updateProgressbar();
    });
});

prevBtns.forEach((btn) => {
    btn.addEventListener("click", () => {
        formStepsNum--;
        updateFormSteps();
        updateProgressbar();
    });
});

function updateFormSteps() {
    formSteps.forEach((formStep) => {
        formStep.classList.contains("form-step1-active") &&
            formStep.classList.remove("form-step1-active");
    });

    formSteps[formStepsNum].classList.add("form-step1-active");
}

function updateProgressbar() {
    progressSteps.forEach((progressStep, idx) => {
        if (idx < formStepsNum + 1) {
            progressStep.classList.add("progress-step1-active");
        } else {
            progressStep.classList.remove("progress-step1-active");
        }
    });

    const progressActive = document.querySelectorAll(".progress-step1-active");

    progress.style.width =
        ((progressActive.length - 1) / (progressSteps.length - 1)) * 100 + "%";
}
function readURL(input) {
    if (input.files && input.files[0]) {
        var reader = new FileReader();

        reader.onload = function (e) {
            $(".image-upload1-wrap").hide();

            $(".file-upload1-image").attr("src", e.target.result);
            $(".file-upload1-content").show();

            $(".image-title").html(input.files[0].name);
        };

        reader.readAsDataURL(input.files[0]);
    } else {
        removeUpload();
    }
}

function removeUpload() {
    $(".file-upload1-input").replaceWith($(".file-upload-input").clone());
    $(".file-upload1-content").hide();
    $(".image-upload1-wrap").show();
}
$(".image-upload1-wrap").bind("dragover", function () {
    $(".image-upload1-wrap").addClass("image-dropping");
});
$(".image-upload1-wrap").bind("dragleave", function () {
    $(".image-upload1-wrap").removeClass("image-dropping");
});




   